function receiveTeams(){$.getJSON("api/teams.json",function(e){var b=$("#teamcontainer");b.html("");for(var d=0;d<e.length;d++){var c='<div class="team">';c+='<div class="first">';c+='<div><span class="rank"><i class="cubes icon"></i>'+e[d].rank+"</span></div>";c+='<div><span class="laps">'+e[d].laps+"</span></div>";c+="</div>";c+='<div class="second">';c+="<div>";c+='<span class="title">'+e[d].name+"</span>";c+='<span class="num titlenum">'+e[d].nr+"</span>";c+="</div>";for(var a=0;a<e[d].drivers.length;a++){c+="<li>";c+='<span class="driver">'+e[d].drivers[a].name+"</span>";c+='<span class="num drivernum">'+e[d].drivers[a].nr+"</span>";c+="</li>"}c+="</div>";c+='<div class="third">';c+='<div><span class="speed">'+e[d].avg+"&nbsp;<sup>km</sup>/<sub>h</sub></span></div>";c+="</div>";c+="</div>";b.append(c)}})}$(document).ready(receiveTeams);